import { useState } from 'react';
import './App.css';

function App() {
  const [list, setList] = useState([]);

  const handleClick = (e) => {

    const newDot = {
      clientX: e.clientX,
      clientY: e.clientY,
    }

    // console.log(newDot); - Here I am checking to see if I can get clientX and clientY

    setList((prev) => [...prev, newDot]);
  }

  return (
    <div id='page' onClick={ handleClick }>
      {JSON.stringify(list)}
      <div className='dot' />
    </div>
  );
}

export default App;
